mutation productCreate($product: ProductCreateInput) {
  productCreate(product: $product) {
    product {
      id
      title
      descriptionHtml
      collections(first: 10) { 
        nodes {
         id
        }
       }
      options {
        id
        name
        position
        optionValues {
          id
          name
          hasVariants
        }
      }
      variants(first: 100) {
        edges {
          node {
            id
            title
            price
            sku
          }
        }
      }
    }
    userErrors {
      field
      message
    }
  }
}

